mapper.select:
select u.avatarurl, u.nickname, (select count(1) from tb_circle where uid = #{uid} group by uid) circleNum,
       (select count(1) from tb_follow where followid = #{uid} group by followid) fansNum,
       (select count(1) from tb_follow where uid = #{uid} group by uid) followNum from tb_user u where uid = #{uid}
